content.query:
CREATE USER [alfortes@microsoft.com] FROM EXTERNAL PROVIDER;
EXEC sp_addrolemember 'db_datareader', 'alfortes@microsoft.com';

CREATE ROLE Msft
EXEC sp_addrolemember 'Msft', 'alfortes@microsoft.com';

-- create View as owner on database table owned by this account
CREATE VIEW wwi.Cities AS  SELECT *  FROM wwi.DimCity
-- grant select on this view to the role
GRANT SELECT ON wwi.Cities TO [Msft]
EXECUTE AS USER = 'alfortes@microsoft.com'; 
SELECT * FROM wwi.Cities
sp_droprolemember 'Msft', 'alfortes@microsoft.com';
-- Execute the following as a database owner  
GRANT SELECT ON wwi.DimCity TO db_datareader WITH GRANT OPTION; 

REVOKE CONTROL ON DATABASE::sqlpool01 TO [alfortes@microsoft.com];

DROP VIEW dbo.Cities
--Delete the workspace MSI user in the database
DROP USER [alfortes@microsoft.com];
